package com.yetta.pilipili.util;

import org.apache.commons.codec.binary.Base64;
import org.springframework.web.multipart.MultipartFile;

import java.io.*;
import java.util.UUID;

public class SaveImg {
    public static String save(String img) throws IOException {

        int a=img.indexOf("/");
        int b=img.indexOf(";");
        int c=img.indexOf(",");

        String fileSuffix=img.substring(a+1, b);
        String file=img.substring(c+1);
        String key= UUID.randomUUID().toString().replace("-", "");
        FileOutputStream out=new FileOutputStream("/home/yetta/Desktop/statics/user_avatar/"+key+"."+fileSuffix);
        byte data[]= Base64.decodeBase64(file);
        out.write(data);
        out.close();
        String filePath="/statics/user_avatar/"+key+"."+fileSuffix;
//        System.out.println(filePath);
        return filePath;
    }
    public static String save(MultipartFile file) throws IOException {

        String fileName=file.getOriginalFilename();

        String suffix=fileName.substring(fileName.lastIndexOf(".")+1);

        String key= UUID.randomUUID().toString().replace("-", "");

        String newFileName="/home/yetta/Desktop/statics/user_avatar/"+key+"."+suffix;

        if (newFileName!=null&&newFileName!=""){
            file.transferTo(new File(newFileName));
        }

        String filePath="/statics/user_avatar/"+key+"."+suffix;
//        System.out.println(filePath);
        return filePath;
    }

    public static void main(String[] args) throws IOException {
        String a="data:image/jpeg;base64,";
        save(a);
    }
}
